import { Link } from 'react-router-dom';
import './css/style.css';
import Card from './componets/Card';
import React, { useState } from 'react';

function App() {
  const [posts, setPosts] = useState();
  const [load, setLoad] = useState(true);

  const fetchServices = async () => {
    const response = await fetch("https://exam.avavion.ru/api/services/");
    const data = await response.json();

    setPosts(data.data)
    setLoad(false)
  }

  React.useEffect(() => {
    fetchServices();
    console.log(posts)
  }, [])

  return (
    <div className="App">
      <div className='container'>
        {
          load === true ? <h1 className='title'>Идет загрузка</h1>
          : posts.map((post) => <Card key={post.id} image={post.image_url} name={post.name} content={post.content} price={post.price}/>)
        }
      </div>
    </div>
  );
}

export default App;
