import React, { useState } from 'react'
import Post from '../componets/Post';

const HomePage = () => {
    const [posts, setPosts] = React.useState();
    const [load, setLoad] = React.useState(true);
    const [searchValue, setSearchValue] = useState("");
    const [select, setSelect] = useState('');

    const onChangeSearchValue  = (value) => {
        setSearchValue(value)
    }

    const onChangeSort = (value) => {
        setSelect(value)
        if (value === 'DESC') {
            console.log(2);
        }

        if (value === 'ASC') {
            console.log(1);
        }
    }

    const fetchServices = async () => {
      const response = await fetch('https://exam.avavion.ru/api/services/');
      const data = await response.json();
  
      setPosts(data.data);
      setLoad(false);
    };
    React.useEffect(() => {
      fetchServices();
    }, []);

    

  return (
    <div>
        <input value={searchValue} onChange={(event) => onChangeSearchValue(event.target.value)} className='search' placeholder='Поиск'/>
        <select className='select' onChange={(event) => onChangeSort(event.target.value)}>
            <option selected value="none">Выбрать сортировку</option>
            <option value="DESC">От большего к меньшему</option>
            <option value="ASC">От меньшего к большему</option>
        </select>
    {load === true ? (
      <h1 className="title">Идет загрузка</h1>
    ) : (
      posts.sort((a,b) => select ? b.price - a.price : a.price - b.price).filter((post) => post.name.toLowerCase().includes(searchValue.toLowerCase()) ).map((post) => (
        <Post
          key={post.id}
          id={post.id}
          image={post.image_url}
          name={post.name}
          content={post.content}
          price={post.price}
        /> 
      ))
    )}
  </div>
  )
}

export default HomePage
