import React from 'react';
import { Link, useParams } from 'react-router-dom';

const PostPage = () => {
  const { id } = useParams();

  const [post, setPost] = React.useState();
  const [load, setLoad] = React.useState(true);

  const fetchServices = async () => {
    const response = await fetch(`https://exam.avavion.ru/api/services/${id}`);
    const data = await response.json();

    setPost(data.data);
    setLoad(false);
  };

  React.useEffect(() => {
    fetchServices();
    console.log(post);
  }, []);

  return (
    <>
      {load ? (
        <h1>Идет загрузка</h1>
      ) : (
        <div className="post">
          <img src={post.image_url} />
          <h2 className={post.name}>Продвижение веб-сайта в ТОП10</h2>
          <div className={post.content}>
            Рекомендуем данный тариф всем типам сайтов, кроме интернет-магазинов. А
            интернет-магазинам рекомендуем продвижение по трафику.
          </div>
          <div className="subtitle">От 100 000 ₽</div>
          <Link to="/" className="link">
            Назад
          </Link>
        </div>
      )}
    </>
  );
};

export default PostPage;
